package InClassExample;

public class iterativeStaments {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 10 ; i >= 3; i--) {
			System.out.print(i +" ");
		}
		
		int j=5;
		while(j < 10) {
			System.out.println(j);
			j++;
		}
		
		
		
		int k=2134;
		do {
			System.out.println(k);
			k--;
		}while(k > 2000); 
			
		

	}

}
